# Nombre de handle dans cette array ?
def array_length(handles)
  return handles.length
end

# Handle le plus court de cette liste ?
def array_min_length(handles)
  handles.min {|a, b| a.length <=> b.length}
end

# Nombre de handle contenant 5 caractères (sans @)
def count_5_char(handles)
  return handles.count { |handle| handle.length == 6 }
end

# Combien commencent par une majuscule ?
def count_maj(handles)
  return handles.count { |handle| handle[1] =~ /[A-Z]/ }
end

# Trie de la liste par ordre alphabétique.
def sort_handles_by_alpha(handles)
  return handles.sort_by { |handle| handle[1].downcase }
end

# Trie la liste par taille des handle (du plus petit au plus grand)
def sort_handles_by_size(handles)
  return handles.sort_by(&:length)
end

# Position du journaliste @epenser ?
def epenser_position(handles)
  return handles.index('@epenser') + 1
end

# Execution de toutes les méthodes
def perform(handles)
  puts "Le nombre de Handles est de : " + array_length(handles).to_s
  puts "Le handle le plus court est : " + array_min_length(handles).to_s
  puts "Le nombre de Handles avec 5 caractères est de : " + count_5_char(handles).to_s
  puts "Le nombre de Handles qui commence par une majuscule : " + count_maj(handles).to_s
  puts "Tri par ordre alphabétique : " + sort_handles_by_alpha(handles).to_s
  puts "Tri par ordre de taille : " + sort_handles_by_size(handles).to_s
  puts "La position du journaliste @epenser est de : " + epenser_position(handles).to_s
end

perform(twitter_handles)
